import { List, Space, Tag, Typography } from 'antd';
import React from 'react';

function DetailInfo({ info, genresLayout }) {
	const { Text, Title } = Typography;

	console.log(genresLayout);

	const data = [
		{
			id: 1,
			title: 'Budget',
			value: info.budget,
		},

		// {
		// 	id: 2,
		// 	title: 'Genres',
		// 	value: genresLayout,
		// },
		{
			id: 3,
			title: 'Home page',
			value: info.homepage,
		},
		{
			id: 4,
			title: 'Popularity',
			value: info.popularity,
		},
		{
			id: 5,
			title: 'Revenue',
			value: info.revenue,
		},
		// {
		// 	id: 6,
		// 	title: 'Spoken Languages',
		// 	value: info.spoken_languages,
		// },
		{
			id: 7,
			title: 'Status',
			value: info.status,
		},
		{
			id: 8,
			title: 'Vote count',
			value: info.vote_count,
		},
	];

	return (
		<List
			itemLayout='horizontal'
			dataSource={data}
			renderItem={(item) => (
				<List.Item key={item.id}>
					<Space>
						<Text>{item.title}</Text>
						<Tag color='red'>{item.value}</Tag>
					</Space>
				</List.Item>
			)}
		/>
	);

	// <div></div>
	// );
}

export default DetailInfo;
