import { Avatar, Card, Divider, List, Space, Tag, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';
import Images from './Images';

function ListView({ current, width, onClick }) {
	const { Title, Text } = Typography;
	const imgUrl = API_KEY.IMAGE_URL;

	//responsive
	if (width < 500) {
		return (
			<List
				grid={{
					gutter: 16,
					xs: 1,
				}}
				itemLayout='horizontal'
				dataSource={current}
				renderItem={(item) => (
					<List.Item className='list-item' onClick={() => onClick(item.id)}>
						<Card cover={<Images src={`${imgUrl + item.poster_path}`} />}>
							<Space direction='vertical' wrap>
								<Title className='movie-title' level={width > 227 ? 3 : 5}>
									{item.title}
								</Title>

								<Space
									style={{ textAlign: 'justify' }}
									size={[12, 12]}
									direction='vertical'
								>
									<Space
										size={width > 227 ? [6, 6] : [2, 2]}
										split={width > 500 ? <Divider type='vertical' /> : null}
										wrap
										className='tag'
									>
										<Tag className='tag-release' color='red'>
											{item.release_date}
										</Tag>
										<Tag color='red'>{item.vote_count}</Tag>
									</Space>
									<Space>
										<Text strong className='imdb-point'>
											IMDb
										</Text>
										<Text strong>{item.vote_average}/10</Text>
									</Space>

									<Text>{item.overview}</Text>
								</Space>
							</Space>
						</Card>
					</List.Item>
				)}
			/>
		);
	}

	return (
		<List
			itemLayout='horizontal'
			size='large'
			dataSource={current}
			renderItem={(item) => (
				<List.Item className='list-item' onClick={() => onClick(item.id)}>
					<List.Item.Meta
						avatar={
							<Avatar
								className='movie-poster'
								shape='square'
								size={250}
								src={`${imgUrl + item.poster_path}`}
							/>
						}
						title={item.title}
						description={
							<>
								<Space size={[12, 12]} direction='vertical'>
									<Space>
										<Tag color='red'>{item.release_date}</Tag>
										<Tag color='red'>{item.vote_count}</Tag>
									</Space>
									<Space>
										<Text strong className='imdb-point'>
											IMDb
										</Text>
										<Text strong>{item.vote_average}/10</Text>
									</Space>

									{item.overview}
								</Space>
							</>
						}
					/>
				</List.Item>
			)}
		/>
	);
}

export default ListView;
