import React, { useEffect } from 'react';
import useGetListCurrentlyPlaying from '../../hooks/useGetListCurrentlyPlaying';
import { Avatar, Divider, List, Skeleton } from 'antd';
import { API_KEY } from '../../constants/api-keys';
function CurrentlyPlayingList(props) {
	// const [loading, setLoading] = useState(false);
	const { loading, currentPlayingList } = useGetListCurrentlyPlaying(1);

	const loadMoreData = () => {
		if (loading) {
			return;
		}

		useEffect(() => {
			loadMoreData();
		}, []);
	};

	return (
		<InfiniteScroll
			dataLength={currentPlayingList.results.length}
			next={loadMoreData}
			hasMore={currentPlayingList.results.length < 50}
			loader={
				<Skeleton
					avatar
					paragraph={{
						rows: 1,
					}}
					active
				/>
			}
			endMessage={<Divider plain>It is all, nothing more ü§ê</Divider>}
			scrollableTarget='scrollableDiv'
		>
			<List
				itemLayout='horizontal'
				dataSource={currentPlayingList?.results}
				renderItem={(item) => (
					<List.Item>
						<List.Item.Meta
							avatar={
								<Avatar
									shape='square'
									size={{
										xs: 24,
										sm: 200,
										md: 200,
										lg: 200,
										xl: 250,
										xxl: 100,
									}}
									src={`${API_KEY.IMAGE_URL + item.poster_path}`}
								/>
							}
							title={item.title}
							description={item.release_date}
						/>
					</List.Item>
				)}
			/>
		</InfiniteScroll>
	);
}

export default CurrentlyPlayingList;
