import { Card, Col, Row } from 'antd';
import Meta from 'antd/lib/card/Meta';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';

function GridView({ current, width, onClick }) {
	console.log(current);
	const imgUrl = API_KEY.IMAGE_URL;
	return (
		<Row gutter={[16, 16]}>
			{current?.map((child) => (
				<Col span={6}>
					<Card
						type='inner'
						hoverable
						cover={<img alt='example' src={`${imgUrl}${child.poster_path}`} />}
					>
						<Meta
							className='meta-grid-layout'
							title={child.title}
							description={child.overview}
						/>
					</Card>
				</Col>
			))}
		</Row>
	);
}

export default GridView;
