import { Space, Tag, Typography } from 'antd';
import React from 'react';
import { useParams } from 'react-router-dom';
import { API_KEY } from '../../constants/api-keys';
import useGetDetailCurrentlyPlaying from '../../hooks/useGetDetailCurrentlyPlaying';

function DetailMovie(props) {
	const params = useParams();
	const imageUrl = API_KEY.IMAGE_URL;
	const { Title, Text } = Typography;
	const { id } = params;

	const { loading, detailCurrentPlaying } = useGetDetailCurrentlyPlaying(id);

	// console.log(detailCurrentPlaying);
	const {
		title,
		poster_path,
		release_date,
		genres,
		runtime,
		overview,
		vote_average,
	} = detailCurrentPlaying;
	const genresLayout = genres?.map((gen) => gen.name).join(', ');
	var hour = Math.floor(runtime / 60);
	var minutes = runtime % 60;

	return (
		<div>
			<Space size={200} align='start'>
				<img className='detail-image' src={`${imageUrl}${poster_path}`} />

				<Space direction='vertical'>
					<Title level={1}>{title}</Title>

					<Space>
						<Tag color='magenta'>{release_date}</Tag>
						<Tag color='red'>{genresLayout}</Tag>
						<Tag color='red'>{`${hour}h ${minutes}m`}</Tag>
					</Space>

					<Space>
						<Title
							style={{ backgroundColor: '#f4c518', padding: '5px' }}
							level={5}
						>
							IMDb
						</Title>
						<Text>{vote_average}</Text>
					</Space>

					<Space direction='vertical'>
						<Title level={5}>Over view</Title>
						<Text>{overview}</Text>
					</Space>
				</Space>
			</Space>
		</div>
	);
}

export default DetailMovie;
