import { List, Space, Tag, Typography } from 'antd';
import React from 'react';

function DetailInfo({ info }) {
	const { Text } = Typography;

	var formatter = new Intl.NumberFormat('en-US', {
		style: 'currency',
		currency: 'USD',
	});

	const convertMoney = formatter.format(info.budget);

	const data = [
		{
			id: 1,
			title: 'Budget',
			value: convertMoney,
		},

		{
			id: 2,
			title: 'Genres',
			value: info.genres,
		},
		{
			id: 3,
			title: 'Home page',
			value: info.homepage,
		},
		{
			id: 4,
			title: 'Popularity',
			value: info.popularity,
		},
		{
			id: 5,
			title: 'Revenue',
			value: info.revenue,
		},
		{
			id: 6,
			title: 'Spoken Languages',
			value: info.spoken_languages,
		},
		{
			id: 7,
			title: 'Status',
			value: info.status,
		},
		{
			id: 8,
			title: 'Vote count',
			value: info.vote_count,
		},
	];

	return (
		<List
			itemLayout='horizontal'
			dataSource={data}
			renderItem={(item) => (
				<List.Item key={item.id}>
					{item.id === 2 && (
						<Space>
							<Text strong>{item.title}:</Text>

							{item.value?.map((child, idx) => (
								<Tag key={idx} color='geekblue'>
									{child.name}
								</Tag>
							))}
						</Space>
					)}

					{item.id === 6 && (
						<Space>
							<Text strong>{item.title}:</Text>

							{item.value?.map((child, idx) => (
								<Tag key={idx} color='geekblue'>
									{child.name}
								</Tag>
							))}
						</Space>
					)}

					{item.id !== 6 && item.id !== 2 && (
						<Space>
							<Text strong>{item.title}:</Text>
							<Tag color='red'>{item.value}</Tag>
						</Space>
					)}
				</List.Item>
			)}
		/>
	);
}

export default DetailInfo;
