import React from 'react';
import useGetListCurrentlyPlaying from '../../hooks/useGetListCurrentlyPlaying';
import { Avatar, List, Skeleton } from 'antd';
import { API_KEY } from '../../constants/api-keys';
function CurrentlyPlayingList(props) {
	const { loading, currentPlayingList } = useGetListCurrentlyPlaying(1);

	console.log(loading, currentPlayingList);
	const newArr = new Array(15).fill(null).map((_, index) => {
		const key = index + 1;
		return {
			key,
			label: `nav ${key}`,
		};
	});

	if (!loading) {
		return (
			<List
				dataSource={newArr}
				renderItem={(item) => (
					<>
						<Skeleton.Avatar
							style={{ marginRight: '30px' }}
							shape='square'
							size={200}
							active={true}
						/>
						<Skeleton active={true} paragraph={{ rows: 4 }} />
					</>
				)}
			/>
		);
	}

	return (
		<List
			itemLayout='horizontal'
			dataSource={currentPlayingList?.results}
			renderItem={(item) => (
				<List.Item>
					<List.Item.Meta
						avatar={
							<Avatar
								shape='square'
								size={{
									xs: 24,
									sm: 200,
									md: 200,
									lg: 200,
									xl: 250,
									xxl: 100,
								}}
								src={`${API_KEY.IMAGE_URL + item.poster_path}`}
							/>
						}
						title={item.title}
						description={item.release_date}
					/>
				</List.Item>
			)}
		/>
	);
}

export default CurrentlyPlayingList;
