import { MoreOutlined } from '@ant-design/icons';
import { Button, Menu, Popover, Space } from 'antd';
import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { itemsMenu } from '../../constants/items-menu';
import useGetView from '../../hooks/useGetView';
import { useView } from '../../hooks/useView';

function MenuDesktop({ width, handleClickNavigateDetail, searchHeader }) {
	const [viewMode, setViewMode] = useView();
	const location = useLocation();
	const { setView } = useGetView();
	const pathname = location.pathname.split('/');

	useEffect(() => {
		setView(viewMode);
	}, [viewMode]);

	console.log(width);

	const content = () => {
		return (
			<>
				{searchHeader}

				<Button
					style={{ marginTop: '10px' }}
					onClick={() => setViewMode(!viewMode)}
					type='primary'
				>
					{!viewMode ? 'List View' : 'Grid View'}
				</Button>
			</>
		);
	};

	return (
		<div>
			{width >= 850 && (
				<Menu
					theme='light'
					mode='horizontal'
					defaultSelectedKeys={pathname[1]}
					onClick={handleClickNavigateDetail}
					items={itemsMenu}
					selectedKeys={pathname[1]}
				/>
			)}

			{width >= 850 ? (
				<>
					<div className='search-header'>{searchHeader}</div>
					<div className='switch-between-view'>
						<Button onClick={() => setViewMode(!viewMode)} type='primary'>
							{!viewMode ? 'List View' : 'Grid View'}
						</Button>
					</div>
				</>
			) : (
				<Popover content={content} placement='bottomRight' trigger='click'>
					<Button icon={<MoreOutlined />} />
				</Popover>
			)}
		</div>
	);
}

export default MenuDesktop;
