import { Avatar, Divider, List, Space, Tag, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';

function CurrentlyPlayingList({ current }) {
	const { Title, Text } = Typography;
	const imgUrl = API_KEY.IMAGE_URL;

	return (
		<List
			itemLayout='horizontal'
			size='large'
			dataSource={current}
			renderItem={(item) => (
				<List.Item>
					<Space size={12} align='start'>
						<Avatar
							className='movie-poster'
							shape='square'
							size={200}
							src={`${imgUrl + item.poster_path}`}
						/>
						<Space direction='vertical'>
							<Title> {item.title}</Title>
							<Space size={[6, 6]} wrap split={<Divider type='vertical' />}>
								<Tag color='red'>{item.release_date}</Tag>
								<Tag color='red'>{item.vote_average}</Tag>
								<Tag color='red'>{item.vote_count}</Tag>
							</Space>
						</Space>
					</Space>
				</List.Item>
			)}
		/>
	);
}

export default CurrentlyPlayingList;
