import { Avatar, Col, Divider, List, Row, Space, Tag, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';

function CurrentlyPlayingList({ current }) {
	const { Title, Text } = Typography;
	const imgUrl = API_KEY.IMAGE_URL;
	console.log(current);

	return (
		<Row gutter={[16, 16]}>
			<List
				itemLayout='horizontal'
				size='large'
				dataSource={current}
				renderItem={(item) => (
					<Col xs={24} sm={12} md={12} lg={12} xl={11}>
						<List.Item>
							<Avatar
								className='movie-poster'
								shape='square'
								size={200}
								src={`${imgUrl + item.poster_path}`}
							/>
							{/* </Col> */}

							{/* <Col xs={24} sm={12} md={12} lg={12} xl={12}> */}
							<Title style={{ color: '#1990ff' }} level={2}>
								{item.title}
							</Title>

							<Space size={[8, 8]} wrap split={<Divider type='vertical' />}>
								<Tag color='red'>{item.release_date}</Tag>
								<Tag color='red'>{item.vote_average}</Tag>
								<Tag color='red'>{item.vote_count}</Tag>
							</Space>
						</List.Item>
					</Col>
				)}
			/>
		</Row>
	);
}

export default CurrentlyPlayingList;
