import { Col, Row, Space, Tag, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';
import useGetGenre from '../../hooks/useGetGenre.jsx';

function ListViewTest({ current }) {
	const { Title, Text } = Typography;
	const imgurl = API_KEY.IMAGE_URL;

	console.log(current);

	function getDifference(genreCurrent, genreList) {
		let newArr = [];
		genreList?.filter((gen) => {
			genreCurrent?.filter((cur) => {
				if (cur === gen.id) {
					newArr.push(gen.name);
				}
			});
		});
		return newArr;
	}

	const { loading, genreList } = useGetGenre();

	if (loading) {
		return <h2>hload///</h2>;
	}

	return (
		<Row gutter={[16, 16]}>
			{current?.map((r) => (
				<Col key={r.id} span={24}>
					<Row key={r.id} gutter={[16, 16]}>
						<Col span={8} xs={24} sm={24} md={8} lg={8} xl={8}>
							<img width={'100%'} src={imgurl + r.poster_path} />
						</Col>
						<Col span={16} xs={24} sm={24} md={16} lg={16} xl={16}>
							<Title>{r.title}</Title>
							<Space>
								<Tag color='geekblue'>{r.release_date}</Tag>
								<Space wrap>
									{getDifference(r.genre_ids, genreList.genres).map(
										(genre, idx) => (
											<Text strong type='danger' key={idx}>
												{genre}
											</Text>
										)
									)}
								</Space>
							</Space>

							<Space direction='vertical'>
								<Text strong className='imdb-point'>
									IMDb
								</Text>
								<Text strong>{r.vote_average}/10</Text>
							</Space>
						</Col>
					</Row>
				</Col>
			))}
		</Row>
	);
}

export default ListViewTest;
