import { Col, Row, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';

function DetailMovieInfo(props) {
	const { Text, Title } = Typography;
	const {
		backdrop_path,
		belongs_to_collection,
		genres,
		homepage,
		overview,
		popularity,
		poster_path,
		production_companies,
		production_countries,
		release_date,
		revenue,
		runtime,
		spoken_languages,
		status,
		tagline,
		title,
		vote_average,
		vote_count,
	} = props;
	const imgUrl = API_KEY.IMAGE_URL;

	const genresLayout = props.genres?.map((gen) => gen.name).join(', ');
	var hour = Math.floor(props.runtime / 60);
	var minutes = props.runtime % 60;

	console.log(props);
	return (
		<Row gutter={[16, 16]}>
			<Col span={10} xs={24} sm={24} md={24} lg={12} xl={6}>
				<img className='movie-poster' src={`${imgUrl}${props.poster_path}`} />
			</Col>

			<Col span={12} xs={24} sm={24} md={24} lg={12} xl={6}>
				<Title level={1}>{props.title}</Title>
				<Row>
					<Col className='tag-detail-content'>
						<Text>{`${hour}h ${minutes}m`}</Text>
					</Col>
					<Col className='tag-detail-content'>
						<Text>{`${hour}h ${minutes}m`}</Text>
					</Col>
				</Row>
			</Col>
		</Row>
	);
}

export default DetailMovieInfo;
