import { Space, Tag, Typography } from 'antd';
import React from 'react';
import { useParams } from 'react-router-dom';
import { API_KEY } from '../../constants/api-keys';
import useGetDetailCurrentlyPlaying from '../../hooks/useGetDetailCurrentlyPlaying';
import DetailMovieInfo from './DetailMovieInfo';

function DetailMovie(props) {
	const params = useParams();
	const imageUrl = API_KEY.IMAGE_URL;
	const { Title, Text } = Typography;
	const { id } = params;

	const { loading, detailCurrentPlaying } = useGetDetailCurrentlyPlaying(id);

	// console.log(detailCurrentPlaying);
	const { genres, runtime } = detailCurrentPlaying;
	const genresLayout = genres?.map((gen) => gen.name).join(', ');
	var hour = Math.floor(runtime / 60);
	var minutes = runtime % 60;

	return (
		<DetailMovieInfo
			{...detailCurrentPlaying}
			hour={hour}
			minutes={minutes}
			genresLayout={genresLayout}
		/>
	);
}

export default DetailMovie;
