import { Row } from 'antd';
import React, { useEffect, useRef } from 'react';
import { API_KEY } from '../../constants/api-keys';
import LazyLoadImg from '../LazyLoad/LazyLoadImg';

function ListViewTest({ image }) {
	// console.log(current);
	const imgRef = useRef();
	useEffect(() => {
		const img = imgRef.current;

		const observer = new IntersectionObserver((entries) => {
			if (entries[0].isIntersecting) {
				img.setAttribute('src', image.url);
				img.classList.add('active');
			}
		});

		if (img) observer.observe(img);

		return () => {
			if (img) observer.unobserve(img);
		};
	}, []);

	return (
		<Row>
			{/* {current?.map((movie) => (
				<div key={movie.id}>
				</div>
			))} */}
			<img loading='lazy' alt={image.url} ref={imgRef} className='lazy-load' />
			{/* <LazyLoadImg url={test != undefined ? test : []} /> */}
		</Row>
	);
}

export default ListViewTest;
