import { Col, List, Row, Space, Tag, Typography } from 'antd';
import React from 'react';

function DetailInfo({ info }) {
	const { Text } = Typography;

	var formatter = new Intl.NumberFormat('en-US', {
		style: 'currency',
		currency: 'USD',
	});

	const convertbudget = formatter.format(info.budget);
	const convertrevenue = formatter.format(info.revenue);

	const data = [
		{
			id: 1,
			title: 'Budget',
			value: convertbudget,
		},

		{
			id: 2,
			title: 'Genres',
			value: info.genres,
		},
		{
			id: 3,
			title: 'Home page',
			value: info.homepage,
		},
		{
			id: 4,
			title: 'Popularity',
			value: info.popularity,
		},
		{
			id: 5,
			title: 'Revenue',
			value: convertrevenue,
		},
		{
			id: 6,
			title: 'Spoken Languages',
			value: info.spoken_languages,
		},
		{
			id: 7,
			title: 'Status',
			value: info.status,
		},
		{
			id: 8,
			title: 'Vote count',
			value: info.vote_count,
		},
	];

	return (
		<List
			itemLayout='horizontal'
			dataSource={data}
			renderItem={(item) => (
				<List.Item key={item.id}>
					<Row gutter={[16, 16]}>
						{item.id === 2 && (
							<Col key={item.id}>
								<Row gutter={[16, 16]}>
									<Col xs={24} sm={5} md={8} lg={8} xl={8}>
										<Text strong>{item.title}:</Text>
									</Col>

									<Col xs={24} sm={19} md={8} lg={8} xl={8}>
										<Space wrap>
											{item.value?.map((child, idx) => (
												<Tag key={item.id} color='geekblue'>
													{child.name}
												</Tag>
											))}
										</Space>
									</Col>
								</Row>
							</Col>
						)}
					</Row>

					{item.id === 6 && (
						<Space wrap>
							<Text strong>{item.title}:</Text>

							{item.value?.map((child, idx) => (
								<Tag key={idx} color='geekblue'>
									{child.name}
								</Tag>
							))}
						</Space>
					)}

					{item.id !== 6 && item.id !== 2 && (
						<Space wrap>
							<Text strong>{item.title}:</Text>
							<Tag color='red'>{item.value}</Tag>
						</Space>
					)}
				</List.Item>
			)}
		/>
	);
}

export default DetailInfo;
