import React, { useEffect, useState } from 'react';
import useGetListCurrentlyPlaying from '../../hooks/useGetListCurrentlyPlaying';
import { Avatar, Divider, List, Skeleton } from 'antd';
import { API_KEY } from '../../constants/api-keys';
import InfiniteScroll from 'react-infinite-scroll-component';

function CurrentlyPlayingList(props) {
	const [loading, setLoading] = useState(false);
	const [data, setData] = useState([]);

	const apikey = API_KEY.KEY;
	const loadMoreData = () => {
		if (loading) {
			return;
		}

		setLoading(true);
		fetch(
			`https://the-ultimate-api-challenge.herokuapp.com/https://api.themoviedb.org/3/movie/now_playing?api_key=${apikey}&language=en-US&page=${1}`
		)
			.then((res) => res.json())
			.then((body) => {
				console.log(body.results);
				setData([...data, ...body.results]);
				setLoading(false);
			})
			.catch(() => {
				setLoading(false);
			});
	};

	useEffect(() => {
		loadMoreData();
	}, []);
	return (
		<div
			id='scrollableDiv'
			style={{
				height: '100vh',
				overflow: 'auto',
				padding: '0 16px',
				border: '1px solid rgba(140, 140, 140, 0.35)',
			}}
		>
			<InfiniteScroll
				dataLength={data.length}
				next={loadMoreData}
				hasMore={data.length < 50}
				loader={
					<Skeleton
						avatar
						paragraph={{
							rows: 1,
						}}
						active
					/>
				}
				endMessage={<Divider plain>It is all, nothing more ü§ê</Divider>}
				scrollableTarget='scrollableDiv'
			>
				<List
					dataSource={data}
					renderItem={(item, idx) => (
						<List.Item key={idx}>
							<List.Item.Meta
								avatar={
									<Avatar src={`${API_KEY.IMAGE_URL + item.poster_path}`} />
								}
								title={item.title}
								// description={item.email}
							/>
						</List.Item>
					)}
				/>
			</InfiniteScroll>
		</div>
	);
}

export default CurrentlyPlayingList;
