import { Avatar, Col, List, Row, Space, Tag, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';
import DetailInfo from './DetailInfo';

function DetailMovieInfo(props) {
	const { Text, Title } = Typography;
	const { width } = props;
	const imgUrl = API_KEY.IMAGE_URL;

	const genresLayout = props.genres?.map((gen) => gen.name).join(', ');
	const hour = Math.floor(props.runtime / 60);
	const minutes = props.runtime % 60;

	const RenderProductionCountries = (product) => {
		return (
			<>
				{props.production_countries?.map(
					(countries, idx) =>
						product.origin_country === countries.iso_3166_1 && (
							<Text key={idx}>{countries.name}</Text>
						)
				)}
			</>
		);
	};

	return (
		<Row gutter={[16, 16]}>
			<Col span={10} xs={24} sm={24} md={8} lg={8} xl={8}>
				<img className='movie-poster' src={`${imgUrl}${props.poster_path}`} />
			</Col>

			<Col span={12} xs={24} sm={24} md={16} lg={16} xl={16}>
				<Row gutter={[16, 16]}>
					<Col span={24}>
						<Title level={width > 672 ? 2 : 4}>{props.title}</Title>
					</Col>

					<Col span={24}>
						<Space wrap>
							<Tag color='geekblue'>{props.release_date}</Tag>

							<Tag color='geekblue'>{`${hour}h ${minutes}m`}</Tag>

							<Tag color='geekblue'>{genresLayout}</Tag>
						</Space>
					</Col>

					<Col span={24}>
						<Space>
							<Text strong className='imdb-point'>
								IMDb
							</Text>
							<Text strong>{props.vote_average}/10</Text>
						</Space>
					</Col>

					<Col>
						<Space direction='vertical'>
							<Title strong level={4}>
								Over view
							</Title>
							<Text>{props.overview}/10</Text>
						</Space>
					</Col>

					<Col span={24}>
						<Row gutter={[16, 16]}>
							<Col span={24}>
								<Title strong level={4}>
									Production companies
								</Title>
							</Col>
							{props.production_companies?.map((product, idx) => (
								<Col
									key={idx}
									xs={24}
									sm={12}
									md={12}
									lg={12}
									xl={6}
									className='production-conpanies'
								>
									<Avatar
										className='avatar-production'
										size='large'
										src={`${imgUrl}${product.logo_path}`}
									/>

									<div className='production-conpanies'>
										<Text strong>{product.name}</Text>
										{RenderProductionCountries(product)}
									</div>
								</Col>
							))}
						</Row>
					</Col>

					<Col span={24}>
						<DetailInfo info={props} genresLayout={genresLayout} />
					</Col>
				</Row>
			</Col>
		</Row>
	);
}

export default DetailMovieInfo;
