import { Col, Row, Space, Tag, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';

function DetailMovieInfo(props) {
	const { Text, Title } = Typography;
	const {
		backdrop_path,
		belongs_to_collection,
		genres,
		homepage,
		overview,
		popularity,
		poster_path,
		production_companies,
		production_countries,
		release_date,
		revenue,
		runtime,
		spoken_languages,
		status,
		tagline,
		title,
		vote_average,
		vote_count,
		width,
	} = props;
	const imgUrl = API_KEY.IMAGE_URL;

	const genresLayout = props.genres?.map((gen) => gen.name).join(', ');
	var hour = Math.floor(props.runtime / 60);
	var minutes = props.runtime % 60;

	console.log(props, width);
	return (
		<Row gutter={[16, 16]}>
			<Col span={10} xs={24} sm={24} md={8} lg={8} xl={8}>
				<img className='movie-poster' src={`${imgUrl}${props.poster_path}`} />
			</Col>

			<Col span={12} xs={24} sm={24} md={16} lg={16} xl={16}>
				<Title level={width > 672 ? 2 : 4}>{props.title}</Title>
				<Space>
					<Tag color='geekblue'>{release_date}</Tag>

					<Tag color='geekblue'>{`${hour}h ${minutes}m`}</Tag>

					<Tag color='geekblue'>{genresLayout}</Tag>
				</Space>

				<div>
					<Text strong className='imdb-point'>
						IMDb
					</Text>
					<Text strong>{vote_average}/10</Text>
				</div>
			</Col>
		</Row>
	);
}

export default DetailMovieInfo;
