import { Breadcrumb, Layout, Menu, Modal, Typography } from 'antd';
import { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import PullToRefresh from 'react-simple-pull-to-refresh';
function MainLayout(props) {
	const { children, searchHeader } = props;
	const { Header, Content, Footer } = Layout;
	const { Title } = Typography;
	const [val, setVal] = useState(false);
	const navigate = useNavigate();
	const location = useLocation();

	const itemsMenu = [
		{
			key: '/now-playing',
			label: 'Now Playing',
		},

		{
			key: '/top-rated',
			label: 'Top Rated',
		},
	];

	const onClick = (e) => {
		if (e.key) {
			navigate(`${e.key}`, { replace: true });
		}
	};

	if (val) {
		window.location.reload();
	}

	if (!navigator.onLine) {
		const error = () => {
			Modal.error({
				centered: true,
				title: 'Network error!!',
				content: 'Please check your internet connection',
				onOk() {
					window.location.reload();
				},
			});
		};
		error();
	}

	return (
		<PullToRefresh
			onRefresh={() => new Promise((resolve) => resolve(setVal(true)))}
		>
			<Layout className='layout'>
				<Header className='page-header'>
					<div className='logo'>
						<img
							style={{ width: '31px', height: '31px' }}
							src={require('../assets/images/video-player.png')}
						/>
						<Title level={3}>Movie Chanel</Title>
					</div>
					<div className='search-header'>{searchHeader}</div>
					<Menu
						theme='light'
						mode='horizontal'
						defaultSelectedKeys={location.pathname}
						onClick={onClick}
						items={itemsMenu}
					/>
				</Header>
				<Content
					style={{
						padding: '0 50px',
					}}
				>
					<Breadcrumb
						style={{
							margin: '16px 0',
						}}
					>
						<Breadcrumb.Item>Home</Breadcrumb.Item>
					</Breadcrumb>
					<div className='site-layout-content'>{children}</div>
				</Content>
				<Footer
					style={{
						textAlign: 'center',
					}}
				>
					Ant Design Â©2018 Created by Ant UED
				</Footer>
			</Layout>
		</PullToRefresh>
	);
}

export default MainLayout;
