import { Space, Typography } from 'antd';
import React from 'react';
import { useParams } from 'react-router-dom';
import { API_KEY } from '../../constants/api-keys';
import useGetDetailCurrentlyPlaying from '../../hooks/useGetDetailCurrentlyPlaying';

function DetailMovie(props) {
	const params = useParams();
	const imageUrl = API_KEY.IMAGE_URL;
	const { Title } = Typography;
	const { id } = params;

	const { loading, detailCurrentPlaying } = useGetDetailCurrentlyPlaying(id);

	// console.log(detailCurrentPlaying);
	const { title, poster_path } = detailCurrentPlaying;

	return (
		<div>
			<Space>
				<img
					className='detail-image'
					src={`${imageUrl}${detailCurrentPlaying.poster_path}`}
				/>

				<Space>
					<Title>{}</Title>
				</Space>
			</Space>
		</div>
	);
}

export default DetailMovie;
