import { Avatar, List, Skeleton, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';
import useGetListCurrentlyPlaying from '../../hooks/useGetListCurrentlyPlaying';

function CurrentlyPlayingList(props) {
	const { Text, Title } = Typography;

	const { loading, currentPlayingList } = useGetListCurrentlyPlaying(1);
	const newArr = new Array(20).fill(null).map((_, index) => {
		const key = index + 1;
		return {
			key,
		};
	});

	if (!loading) {
		return (
			<List
				dataSource={newArr}
				renderItem={() => (
					<List.Item>
						<Skeleton.Image
							className='skeleton-avatar'
							shape='square'
							active={true}
							size='large'
						/>
						<Skeleton active={true} paragraph={{ rows: 4 }} />
					</List.Item>
				)}
			/>
		);
	}

	return (
		<List
			itemLayout='horizontal'
			dataSource={currentPlayingList?.results}
			renderItem={(item) => (
				<List.Item>
					<List.Item.Meta
						avatar={
							<Avatar
								shape='square'
								size={{
									xs: 24,
									sm: 200,
									md: 200,
									lg: 200,
									xl: 300,
									xxl: 300,
								}}
								src={`${API_KEY.IMAGE_URL + item.poster_path}`}
							/>
						}
						title={<Title level={4}>{item.title}</Title>}
						description={item.release_date}
					/>
				</List.Item>
			)}
		/>
	);
}

export default CurrentlyPlayingList;
