import { MenuFoldOutlined, MenuUnfoldOutlined } from '@ant-design/icons';
import { Drawer, Layout, Space, Typography } from 'antd';
import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import MenuDesktop from '../components/MenuHeader/MenuDesktop';

function HeaderLayout({ width }) {
	const [collapsed, setCollapsed] = useState(false);
	const { Header } = Layout;
	const { Title } = Typography;
	const location = useLocation();
	const pathname = location.pathname.split('/');

	return (
		<Header className='page-header'>
			{width < 850 &&
				React.createElement(collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
					className: 'trigger',
					onClick: () => setCollapsed(!collapsed),
				})}

			<Link to={'/now-playing'}>
				<div className='logo'>
					<img
						style={{ width: '31px', height: '31px' }}
						src={require('../assets/images/video-player.png')}
					/>
					<Title style={{ marginLeft: '10px' }} level={4}>
						Movie Chanel
					</Title>
				</div>
			</Link>

			<Drawer
				placement='left'
				title='Basic Drawer'
				closable={false}
				onClose={() => setCollapsed(false)}
				visible={collapsed}
				width={250}
			/>

			{width > 850 && (
				<Space align='center'>
					<MenuDesktop />
				</Space>
			)}
		</Header>
	);
}

export default HeaderLayout;
