import { Pagination } from 'antd';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import useGetListCurrentlyPlaying from '../../hooks/useGetListCurrentlyPlaying';
import useSearch from '../../hooks/useSearch';
import { useView } from '../../hooks/useView';
import CurrentlyPlayingList from './CurrentlyPlayingList';
import SkeletonLoading from './SkeletonLoading';

function CurrentlyPlayingInfo({ width }) {
	const [current, setCurrent] = useState(1);
	const [viewMode] = useView();
	const { search } = useSearch();
	const navigate = useNavigate();
	const { loading, currentPlayingList } = useGetListCurrentlyPlaying(
		current,
		search
	);

	if (loading) {
		return <SkeletonLoading width={width} />;
	}

	console.log(viewMode);

	const onChange = (page) => {
		setCurrent(page);
		window.scrollTo(0, 0);
	};

	const onClick = (item) => {
		navigate(`/now-playing/${item}`);
	};

	return (
		<>
			<Pagination
				className='pagination-currently-list-top'
				responsive={true}
				showSizeChanger={false}
				current={current}
				onChange={onChange}
				total={currentPlayingList?.total_pages}
			/>
			<CurrentlyPlayingList
				onClick={onClick}
				width={width}
				current={currentPlayingList.results}
			/>

			<Pagination
				className='pagination-currently-list-bottom'
				responsive={true}
				showSizeChanger={false}
				current={current}
				onChange={onChange}
				total={currentPlayingList?.total_pages}
			/>
		</>
	);
}

export default CurrentlyPlayingInfo;
