import { Card, List, Skeleton } from 'antd';
import React from 'react';

function SkeletonLoading({ width }) {
	const emptyArray = new Array(20).fill(null).map((_, index) => {
		const key = index + 1;
		return {
			key,
		};
	});

	if (width < 500) {
		return (
			<List
				dataSource={emptyArray}
				renderItem={() => (
					<List.Item>
						<Card cover={<Skeleton.Avatar shape='square' size={250} />}>
							<Skeleton active={true} paragraph={{ rows: 3 }} />
						</Card>
					</List.Item>
				)}
			/>
		);
	}

	return (
		<List
			dataSource={emptyArray}
			renderItem={() => (
				<List.Item>
					<Skeleton.Avatar
						className='skeleton-avatar'
						shape='square'
						active={true}
						size={width > 672 ? 200 : 150}
					/>
					<Skeleton active={true} paragraph={{ rows: 3 }} />
				</List.Item>
			)}
		/>
	);
}

export default SkeletonLoading;
