import { Avatar, Col, Image, List, Row, Space, Tag, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';

function CurrentlyPlayingList({ current }) {
	const { Title, Text } = Typography;
	const imgUrl = API_KEY.IMAGE_URL;
	console.log(current);

	return (
		<List
			itemLayout='horizontal'
			size='large'
			dataSource={current}
			renderItem={(item) => (
				<List.Item>
					<Row gutter={[16]}>
						<Col>
							<Avatar
								className='movie-poster'
								shape='square'
								size={200}
								src={`${imgUrl + item.poster_path}`}
							/>
						</Col>

						<Col>
							<Title style={{ color: '#1990ff' }} level={2}>
								{item.title}
							</Title>

							<Space size={[8, 8]} wrap split={<Divider type='vertical' />}>
								{/* <Row gutter={[16 ,16]}> */}
								{/* <Col xs={24} sm={12} md={12} lg={12} xl={6}> */}
								<Space>
									<Text strong>Release date:</Text>
									<Tag color='red'>{item.release_date}</Tag>
								</Space>
								{/* </Col> */}

								{/* <Col xs={24} sm={12} md={12} lg={12} xl={6}> */}
								<Space>
									<Text strong>vote average:</Text>
									<Tag color='red'>{item.vote_average}</Tag>
								</Space>
								{/* </Col> */}

								{/* <Col xs={24} sm={12} md={12} lg={12} xl={6}> */}
								<Space>
									<Text strong>vote count:</Text>
									<Tag color='red'>{item.vote_count}</Tag>
								</Space>
								{/* </Col> */}
								{/* </Row> */}
							</Space>
						</Col>
					</Row>
				</List.Item>
			)}
		/>
	);
}

export default CurrentlyPlayingList;
