import { Avatar, List, Skeleton, Space, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';
import useGetListCurrentlyPlaying from '../../hooks/useGetListCurrentlyPlaying';

function CurrentlyPlayingList({ width }) {
	const { Text, Title } = Typography;

	const { loading, currentPlayingList } = useGetListCurrentlyPlaying(1);
	const newArr = new Array(20).fill(null).map((_, index) => {
		const key = index + 1;
		return {
			key,
		};
	});

	console.log(currentPlayingList);

	if (loading) {
		return (
			<List
				dataSource={newArr}
				renderItem={() => (
					<List.Item>
						<Skeleton.Avatar
							className='skeleton-avatar'
							shape='square'
							active={true}
							size={width > 672 ? 200 : 150}
						/>
						<Skeleton active={true} paragraph={{ rows: 3 }} />
					</List.Item>
				)}
			/>
		);
	}

	return (
		<List
			itemLayout='horizontal'
			dataSource={currentPlayingList?.results}
			renderItem={(item) => (
				<List.Item>
					<List.Item.Meta
						avatar={
							<Avatar
								shape='square'
								size={{
									xs: 24,
									sm: 200,
									md: 200,
									lg: 200,
									xl: 300,
									xxl: 300,
								}}
								src={`${API_KEY.IMAGE_URL + item.poster_path}`}
							/>
						}
						title={
							<Title style={{ color: '#1990ff' }} level={3}>
								{item.title}
							</Title>
						}
						description={
							<Space direction='vertical'>
								<Space>
									<Space size={5}>
										<Text strong type='success'>
											Release date:
										</Text>
										<Text strong>{item.release_date}</Text>
									</Space>

									<div className='space' />

									<Text strong>vote average: {item.vote_average}</Text>
									<div className='space' />

									<Text strong>vote count: {item.vote_count}</Text>
								</Space>
								<Text>{item.overview}</Text>
							</Space>
						}
					/>
				</List.Item>
			)}
		/>
	);
}

export default CurrentlyPlayingList;
