import { Avatar, Card, Divider, List, Space, Tag, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';

function CurrentlyPlayingList({ current, width }) {
	const { Title, Text } = Typography;
	const imgUrl = API_KEY.IMAGE_URL;

	if (width < 620) {
		return (
			<List
				grid={{
					gutter: 16,
					xs: 1,
				}}
				itemLayout='horizontal'
				dataSource={current}
				renderItem={(item) => (
					<List.Item>
						<Card cover={<img src={`${imgUrl + item.poster_path}`} />}>
							<Space direction='vertical' wrap>
								<Title className='movie-title' level={3}>
									{item.title}
								</Title>

								<Space
									// style={{ textAlign: 'justify' }}
									size={[12, 12]}
									direction='vertical'
								>
									<Space size={[6, 6]} split={<Divider type='vertical' />}>
										<Tag color='red'>{item.release_date}</Tag>
										<Tag color='red'>{item.vote_average}</Tag>
										<Tag color='red'>{item.vote_count}</Tag>
									</Space>

									<Text>{item.overview}</Text>
								</Space>
							</Space>
						</Card>
					</List.Item>
				)}
			/>
		);
	}
	return (
		<List
			itemLayout='horizontal'
			size='large'
			dataSource={current}
			renderItem={(item) => (
				<List.Item>
					<List.Item.Meta
						avatar={
							<Avatar
								className='movie-poster'
								shape='square'
								size={200}
								src={`${imgUrl + item.poster_path}`}
							/>
						}
						title={
							<Title level={3} className='movie-title'>
								{item.title}
							</Title>
						}
						description={
							<>
								<Space size={[12, 12]} direction='vertical'>
									<Space size={[6, 6]} split={<Divider type='vertical' />}>
										<Tag color='red'>{item.release_date}</Tag>
										<Tag color='red'>{item.vote_average}</Tag>
										<Tag color='red'>{item.vote_count}</Tag>
									</Space>

									{item.overview}
								</Space>
							</>
						}
					/>
				</List.Item>
			)}
		/>
	);
}

export default CurrentlyPlayingList;
