import { Avatar, List, Skeleton, Space, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';
import useGetListCurrentlyPlaying from '../../hooks/useGetListCurrentlyPlaying';
import SkeletonLoading from './SkeletonLoading';

function CurrentlyPlayingList({ width }) {
	const { Text, Title } = Typography;

	const { loading, currentPlayingList } = useGetListCurrentlyPlaying(1);

	if (loading) {
		return <SkeletonLoading />;
	}

	return (
		<List
			itemLayout='horizontal'
			dataSource={currentPlayingList?.results}
			renderItem={(item) => (
				<List.Item>
					<List.Item.Meta
						avatar={
							<Avatar
								shape='square'
								size={{
									xs: 24,
									sm: 200,
									md: 200,
									lg: 200,
									xl: 300,
									xxl: 300,
								}}
								src={`${API_KEY.IMAGE_URL + item.poster_path}`}
							/>
						}
						title={
							<Title style={{ color: '#1990ff' }} level={3}>
								{item.title}
							</Title>
						}
						description={
							<Space direction='vertical'>
								<Space>
									<Space size={5}>
										<Text strong type='success'>
											Release date:
										</Text>
										<Text strong>{item.release_date}</Text>
									</Space>

									<div className='space' />

									<Space size={5}>
										<Text strong type='success'>
											vote average:
										</Text>
										<Text strong> {item.vote_average}</Text>
									</Space>

									<div className='space' />
									<Space size={5}>
										<Text strong type='success'>
											vote count:
										</Text>
										<Text strong>{item.vote_count}</Text>
									</Space>
								</Space>

								<Text>{item.overview}</Text>
							</Space>
						}
					/>
				</List.Item>
			)}
		/>
	);
}

export default CurrentlyPlayingList;
