import { Col, Image, Row, Typography } from 'antd';
import React from 'react';
import { API_KEY } from '../../constants/api-keys';
import LazyLoadImg from '../LazyLoad/LazyLoadImg';
import { LazyLoadImage } from 'react-lazy-load-image-component';
import useGetGenre from '../../hooks/useGetGenre.jsx';

function ListViewTest({ current }) {
	const { Title, Text } = Typography;
	const imgurl = API_KEY.IMAGE_URL;

	function getDifference(genreCurrent, genreList) {
		const keys = genreList?.reduce(
			(acc, curr) => ((acc[curr.id] = true), acc),
			{}
		);
		const missing = genreCurrent?.filter((item) => !keys[item.id]);
		return missing;
	}

	const { loading, genreList } = useGetGenre();

	if (loading) {
		return <h2>hload///</h2>;
	}

	// const difference = [...getDifference(genreCurrent, genreList)];

	return (
		<Row gutter={[16, 16]}>
			{current?.map((r) => (
				<Col key={r.id} span={24}>
					{getDifference(r.genre_ids, genreList.genres)}
					<Row key={r.id} gutter={[16, 16]}>
						<Col span={8} xs={24} sm={24} md={8} lg={8} xl={8}>
							<img width={'100%'} src={imgurl + r.poster_path} />
						</Col>
						<Col span={16} xs={24} sm={24} md={16} lg={16} xl={16}>
							<Title>{r.title}</Title>
						</Col>
					</Row>
				</Col>
			))}
		</Row>
	);
}

export default ListViewTest;
